import React, { Component } from 'react';

export class NewsItem extends Component {
  render() {
    // Destructure props for better readability
    let { title, description, imageUrl, newsUrl, author, date, source } = this.props;

    return (
      <div className='container'>
        <div className='my-3'>
          <div className="card" style={{ width: "18rem" }}>
            {/* Default image handling */}
            <img 
              src={!imageUrl ? "https://i.insider.com/67b1efa46630eb10385ccaa7?width=1200&format=jpeg" : imageUrl} 
              className="card-img-top" 
              alt="News thumbnail" 
            />

            {/* Source badge */}
            <span 
              className="position-absolute top-0 translate-middle badge rounded-pill bg-danger" 
              style={{ left: '90%', zIndex: '1' }}
            >
              {source}
            </span>

            <div className="card-body">
              {/* Title */}
              <h5 className="card-title">{title}</h5>

              {/* Description */}
              <p className="card-text">{description}</p>

              {/* Author and date */}
              <p className="card-text">
                <small className="text-body-secondary">
                  By {!author ? "Unknown" : author} on {new Date(date).toGMTString()}
                </small>
              </p>

              {/* Read more link */}
              <a href={newsUrl} target="_blank" rel="noopener noreferrer" className="btn btn-sm btn-dark">
                Read more
              </a>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default NewsItem;
