import React, { Component } from 'react';
import NewsItem from './NewsItem';


export class News extends Component {



  constructor() {
    super();

    this.state = {
      articles: [],
      loading: false,
      page:1
    }



  }
  //async are use to the data
  async componentDidMount() {
    console.log("cdm");
    let url = "https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=107632b20b124b7c81523cabd47635ee&page=1&pageSize=20";
    let data = await fetch(url)
    let parsedData = await data.json();
    console.log(parsedData);
    this.setState({ articles: parsedData.articles ,
      totalResults:parsedData.totalResults
    })
  }

  handleNextClick=async()=>{
  console.log("Next");
  if(this.state.page+1 >Math.ceil(this.state.totalResults/20)){

  }
  else{
  let url = `https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=107632b20b124b7c81523cabd47635ee&page=${ this.state.page + 1}&pageSize=20`;
  let data = await fetch(url)
  let parsedData = await data.json();
  console.log(parsedData);

  this.setState({
    page: this.state.page + 1,
    articles: parsedData.articles 

  })
  }}
  handlePreviousClick=async()=>{

    let url = `https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=107632b20b124b7c81523cabd47635ee&page=${ this.state.page - 1}${this.props.pageSuze}`;
  let data = await fetch(url)
  let parsedData = await data.json();
  console.log(parsedData);

  this.setState({
    page: this.state.page - 1,
    articles: parsedData.articles 
  })
  };
  render() {
    return (
      <div className='container my-3 '>
        <h2>NewsPlace - Top Headline</h2>

        <div className='row' >
          {this.state.articles.map((element) => {
            return <div className='col-md-4' key={element.url}>
              <NewsItem title={element.title ? element.title : ""}
                description={element.description ? element.description : ""}
                imageUrl={element.urlToImage} newsUrl={element.url} />
            </div>
          })}



        </div>
        <div className="d-flex justify-content-between">
        <button disabled={this.state.page<=1}  type="button" className="btn btn-dark" onClick={this.handlePreviousClick}>&larr; Previous</button>
        <button type="button" className="btn btn-dark" onClick={this.handleNextClick}>Next &rarr;</button>
        </div>
      </div>

    )
  }
}
export default News
