import React, { Component } from 'react'
import NewsItem from './NewsItem'


export class News extends Component {



  constructor() {
    super();

    this.state = {
      articles: [],
      loading: false
    }



  }
//async are use to featch the data
 async componentDidMount() {
    console.log("cdm");
    let url="https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=107632b20b124b7c81523cabd47635ee";
    let data=await fetch(url)
    let parsedData= await data.json();
    console.log(parsedData);
    this.setState({articles: parsedData.articles})
  }
  render() {
    return (
      <div className='container my-3 '>
        <h2>NewsPlace - Top Headline</h2>

        <div className='row' >
          {this.state.articles.map((element) => {
            return <div className='col-md-4' key={element.url}>
              <NewsItem title={element.title? element.title.slice(0,45):""} description={element.description? element.description.slice(0,88):""} imageUrl={element.urlToImage} newsUrl={element.url? element.url:"https://i.insider.com/67b1efa46630eb10385ccaa7?width=1200&format=jpeg"} />
            </div>
          })}



        </div>
      </div>

    )
  }
}
export default News
