import React, { Component } from 'react'
import NewsItem from './NewsItem'


export class News extends Component {



  constructor() {
    super();

    this.state = {
      articles: [],
      loading: false,
      page:1
    }



  }
  //async are use to the data
  async componentDidMount() {
    console.log("cdm");
    let url = "https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=107632b20b124b7c81523cabd47635ee&page=1";
    let data = await fetch(url)
    let parsedData = await data.json();
    console.log(parsedData);
    this.setState({ articles: parsedData.articles })
  }

  handleNextClick=async()=>{
  console.log("Next");
  let url = `https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=107632b20b124b7c81523cabd47635ee&page=${ this.state.page + 1}`;
  let data = await fetch(url)
  let parsedData = await data.json();
  console.log(parsedData);
  this.setState({ articles: parsedData.articles })
  this.setState({
    page: this.state.page + 1,

  })
  };
  handlePreviousClick=async()=>{
    console.log("previous");
  };
  render() {
    return (
      <div className='container my-3 '>
        <h2>NewsPlace - Top Headline</h2>

        <div className='row' >
          {this.state.articles.map((element) => {
            return <div className='col-md-4' key={element.url}>
              <NewsItem title={element.title ? element.title : ""}
                description={element.description ? element.description : ""}
                imageUrl={element.urlToImage} newsUrl={element.url} />
            </div>
          })}



        </div>
        <div class="d-flex justify-content-between">
        <button disabled={this.state.page<=1}  type="button" class="btn btn-dark" onClick={this.handlePreviousClick}>&larr; Previous</button>
        <button type="button" class="btn btn-dark" onClick={this.handleNextClick}>Next &rarr;</button>
        </div>
      </div>

    )
  }
}
export default News
