import React, { Component } from 'react';
import NewsItem from './NewsItem';

export class News extends Component {
  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false,
      page: 1,
    };
  }

  async componentDidMount() {
    this.fetchArticles();
  }

  // Arrow function to ensure `this` is bound correctly
  fetchArticles = async () => {
    this.setState({ loading: true });
    const url = `https://newsapi.org/v2/top-headlines?country=us&category=business&apiKey=YOUR_API_KEY&page=${this.state.page}`;
    try {
      let data = await fetch(url);
      let parsedData = await data.json();
      this.setState({
        articles: parsedData.articles,
        loading: false,
      });
    } catch (error) {
      console.error("Error fetching news:", error);
      this.setState({ loading: false });
    }
  };

  // Arrow function for next button click handler
  handleNextClick = async () => {
    // Update the page number first
    await this.setState((prevState) => ({ page: prevState.page + 1 }));
    this.fetchArticles();
  };

  // Arrow function for previous button click handler
  handlePreviousClick = async () => {
    // Update the page number first
    await this.setState((prevState) => ({ page: prevState.page - 1 }));
    this.fetchArticles();
  };

  render() {
    return (
      <div className="container my-3">
        <h2>NewsPlace - Top Headlines</h2>
        {this.state.loading ? (
          <div>Loading...</div>
        ) : (
          <div className="row">
            {this.state.articles.map((element) => (
              <div className="col-md-4" key={element.url}>
                <NewsItem
                  title={element.title || ''}
                  description={element.description || ''}
                  imageUrl={element.urlToImage}
                  newsUrl={element.url}
                />
              </div>
            ))}
          </div>
        )}
        <div className="d-flex justify-content-between">
          <button
            disabled={this.state.page <= 1}
            type="button"
            className="btn btn-dark"
            onClick={this.handlePreviousClick}
          >
            &larr; Previous
          </button>
          <button
            type="button"
            className="btn btn-dark"
            onClick={this.handleNextClick}
          >
            Next &rarr;
          </button>
        </div>
      </div>
    );
  }
}

export default News;
