import React, { Component } from 'react';
import NewsItem from './NewsItem';
import Spinner from './spinner';
import PropTypes from 'prop-types';

export class News extends Component {
static defaultProps = {
  country:"in",
  pageSize:5

};
static propTypes = {
  country:PropTypes.string,
}

  constructor() {
    super();

    this.state = {
      articles: [],
      loading: false,
      page:1
    }



  }
  async Update(){
    let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=107632b20b124b7c81523cabd47635ee&pageSize=${this.state.page}&pageSize${this.props.pageSize}`;
    this.setState({loading: true});
    let data = await fetch(url)
    let parsedData = await data.json();
    console.log(parsedData);
    this.setState({ articles: parsedData.articles ,
      totalResults:parsedData.totalResults,
      loading:false
    })
  }
  //async are use to the data
  async componentDidMount() {
    // console.log("cdm");
    // let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=107632b20b124b7c81523cabd47635ee&pageSize=${this.props.pageSize}`;
    // this.setState({loading: true});
    // let data = await fetch(url)
    // let parsedData = await data.json();
    // console.log(parsedData);

  this.setState({

  })
    this.Update();
  }

  handleNextClick=async()=>{
  // console.log("Next");
  // if(this.state.page+1 >Math.ceil(this.state.totalResults/this.props.pageSize)){

  // }
  // else{
  // let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=107632b20b124b7c81523cabd47635ee&page=${ this.state.page + 1}&pageSize=${this.props.pageSize}`;
  // this.setState({loading:true})
  // let data = await fetch(url)
  // let parsedData = await data.json();
  // console.log(parsedData);

  // this.setState({
  //   page: this.state.page + 1,
  //   articles: parsedData.articles ,
  //   loading:false

  // })
  // }

  this.setState({page: this.state.page+1})
  this.Update();
  }
  handlePreviousClick=async()=>{

  //   let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=107632b20b124b7c81523cabd47635ee&page=${ this.state.page - 1}&pageSize=${this.props.pageSize}`;
  // this.setState({loading:true})
  //   let data = await fetch(url)
  // let parsedData = await data.json();
  // console.log(parsedData);

  // this.setState({
  //   page: this.state.page - 1,
  //   articles: parsedData.articles ,
  //   loading:false
  // })
  this.setState({page: this.state.page-1})
  this.Update();
  };
  render() {
    return (
      <div className='container my-3 '>
        <heading>
        <h2 className='text-center' style={{margin:"30px"}} >NewsPlace - Top Headline</h2></heading>
     {this.state.loading &&  <Spinner/>}

        <div className='row' >
          {this.state.articles.map((element) => { 
            return <div className='col-md-4' key={element.url}>
              <NewsItem title={element.title ? element.title : ""}
                description={element.description ? element.description : ""}
                imageUrl={element.urlToImage} newsUrl={element.url}  author={element.author} date={element.publishedAt}  source={element.source.name}/>
            </div>
          })}



        </div>
        <div className="d-flex justify-content-between">
        <button disabled={this.state.page<=1}  type="button" className="btn btn-dark" onClick={this.handlePreviousClick}> &larr; Previous</button>
        <button type="button" disabled={this.state.page>= Math.ceil(this.state.totalResults/this.props.pageSize)
        } className="btn btn-dark" onClick={this.handleNextClick}>Next &rarr;</button>
        </div>
      </div>

    )
  }
}
export default News
